"use strict";const button=document.querySelector("#register");button.addEventListener("click",function(){var e=document.querySelector("#email");const s=document.querySelector(".top-bar");var r={lot:1,email:e.value};""!==e.value&&(s.classList.contains("success")?s.classList.remove("success","show"):s.classList.contains("error")?s.classList.remove("error","show"):s.classList.contains("warning")&&s.classList.remove("warning","show"),fetch("https://sorteos.hablemosdecodigo.com/api.php",{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify(r)}).then(e=>e.json()).then(e=>{switch(e.status){case"success":document.querySelector("#msg").innerHTML="Te has registrado satisfactoriamente. Gracias por participar.",s.classList.add("success","show");break;case"error":document.querySelector("#msg").innerHTML="Ha ocurrido un error registrando tu inscripción, por favor intentalo nuevamente.",s.classList.add("error","show");break;case"registered":document.querySelector("#msg").innerHTML="Ya te has registrado anteriormente. Gracias por participar.",s.classList.add("error","show");break;case"invalid":document.querySelector("#msg").innerHTML="Revisa si has ingresado un correo válido e intentalo de nuevo.",s.classList.add("warning","show")}}).catch(()=>{document.querySelector("#msg").innerHTML="Ha ocurrido un error registrando tu inscripción, por favor intentalo nuevamente.",s.classList.add("error","show")}))});